mapper.insert:

	SELECT 'BOD'||LPAD(BOARD_SEQ.NEXTVAL, 6, 0)
	FROM DUAL

INSERT INTO BOARD(
	  BOARD_NO
	, MEM_ID
	, BOARD_SUB
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_DATE
	, BOARD_HITS
) VALUES (
	#{boardNo,jdbcType=VARCHAR}
	, #{memId,jdbcType=VARCHAR}
	, #{boardSub,jdbcType=VARCHAR}
	, #{boardTitle,jdbcType=VARCHAR}
	, #{boardContent,jdbcType=CLOB}
	, SYSDATE
	, 0
)

mapper.delete:
UPDATE BOARD
   SET BOARD_DEL_DATE  = SYSDATE
       , BOARD_TITLE   = #{boardTitle,jdbcType=VARCHAR}
       , BOARD_CONTENT = #{boardContent,jdbcType=VARCHAR}
 WHERE BOARD_NO        = #{boardNo, jdbcType=VARCHAR}

mapper.update:
UPDATE BOARD
   SET BOARD_HITS = BOARD_HITS+1
 WHERE BOARD_NO   = #{boardNo,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO LIKE_BOARD(
	 BOARD_NO
	 , MEM_ID
	 , LIKE_TYPE
) VALUES (
	#{boardNo}
	, #{memId}
	, #{likeType}
)

mapper.select:
SELECT COUNT(*)
  FROM LIKE_BOARD
 WHERE BOARD_NO = #{boardNo}
   AND MEM_ID = #{memId}

SELECT LIKE_TYPE
  FROM LIKE_BOARD
 WHERE BOARD_NO = #{boardNo}
   AND MEM_ID   = #{memId}

mapper.update:
UPDATE BOARD B
   SET B.REPLY_CNT = (SELECT COUNT(REPLY_NO) FROM BOARD_REPLY WHERE BOARD_NO = #{boardNo})
 WHERE B.BOARD_NO = #{boardNo}
