mapper.select:
WITH ORDEREDBOARD AS( SELECT B.BOARD_NO, B.MEM_ID, B.BOARD_SUB, B.BOARD_TITLE, SUBSTR(B.BOARD_CONTENT,1,30) BOARD_CONTENT, TO_CHAR(B.BOARD_DATE,'YYYY-MM-DD') BOARD_DATE, B.BOARD_HITS, NVL((SELECT COUNT(D.LIKE_TYPE) LIKE_TYPE FROM LIKE_BOARD D WHERE D.BOARD_NO = B.BOARD_NO GROUP BY D.BOARD_NO),0) LIKE_CNT FROM BOARD B
ORDER BY B.BOARD_NO DESC ) SELECT A.* FROM ( SELECT ROWNUM RNUM, ORDEREDBOARD.* , count(1) over() as totalCnt FROM ORDEREDBOARD ) A
A.RNUM BETWEEN #{startRow} AND #{endRow}

mapper.insert:

	SELECT 'BOD'||LPAD(BOARD_SEQ.NEXTVAL, 6, 0)
	FROM DUAL

INSERT INTO BOARD(
	  BOARD_NO
	, MEM_ID
	, BOARD_SUB
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_DATE
	, BOARD_HITS
) VALUES (
	#{boardNo,jdbcType=VARCHAR}
	, #{memId,jdbcType=VARCHAR}
	, #{boardSub,jdbcType=VARCHAR}
	, #{boardTitle,jdbcType=VARCHAR}
	, #{boardContent,jdbcType=CLOB}
	, SYSDATE
	, 0
)

mapper.delete:
UPDATE BOARD
   SET BOARD_DEL_DATE  = SYSDATE
       , BOARD_TITLE   = #{boardTitle,jdbcType=VARCHAR}
       , BOARD_CONTENT = #{boardContent,jdbcType=VARCHAR}
 WHERE BOARD_NO        = #{boardNo, jdbcType=VARCHAR}

mapper.update:
UPDATE BOARD
   SET BOARD_HITS = BOARD_HITS+1
 WHERE BOARD_NO   = #{boardNo,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO LIKE_BOARD(
	 BOARD_NO
	 , MEM_ID
	 , LIKE_TYPE
) VALUES (
	#{boardNo}
	, #{memId}
	, #{likeType}
