mapper.select:
SELECT COUNT(*)
  FROM LIKE_BOARD
 WHERE BOARD_NO = #{boardNo}
   AND MEM_ID = #{memId}

SELECT LIKE_TYPE
  FROM LIKE_BOARD
 WHERE BOARD_NO = #{boardNo}
   AND MEM_ID   = #{memId}
