mapper.insert:

	SELECT 'EXE' || LPAD(EXEVENT_SEQ.NEXTVAL,6,0) FROM DUAL

INSERT INTO exevent (
    exevent_id,
    exevent_name,
    exevent_content,
    exevent_start,
    exevent_end
) VALUES (
	 #{exeventId,jdbcType=VARCHAR}
	, #{exeventName,jdbcType=VARCHAR}
	, #{exeventContent,jdbcType=VARCHAR}
	, #{exeventStart,jdbcType=DATE}
	, #{exeventEnd,jdbcType=DATE}
)
