mapper.select:
SELECT COUNT(*)
FROM INTEREST
WHERE MEM_ID = #{memId} AND PR_NO = #{prNo}

mapper.insert:

	SELECT INTE_SEQ.NEXTVAL
	FROM DUAL

	INSERT INTO INTEREST (
    INTE_NO,
    INTE_DATE,
    PR_NO,
    MEM_ID
) VALUES (
    #{inteNo,jdbcType=NUMERIC}
    , SYSDATE
    , #{prNo,jdbcType=NUMERIC}
    , #{memId,jdbcType=VARCHAR}
)

mapper.delete:
DELETE
FROM INTEREST
WHERE MEM_ID = #{memId} AND PR_NO = #{prNo}
