mapper.insert:
	INSERT INTO member (
    mem_id,
    mem_pass,
    mem_name,
    mem_use,
    mem_auth_cd
	) VALUES (
	 #{memId,jdbcType=VARCHAR}
	, #{memPass,jdbcType=VARCHAR}
	, #{memName,jdbcType=VARCHAR}
	, 'Y'
	, 'SEEKER'
	)

	INSERT INTO member (
    mem_id,
    mem_pass,
    mem_name,
    mem_use,
    mem_auth_cd
	) VALUES (
	 #{memId,jdbcType=VARCHAR}
	, #{memPass,jdbcType=VARCHAR}
	, #{memName,jdbcType=VARCHAR}
	, 'Y'
	, 'INCRUITER'
	)

	INSERT INTO seeker (
    mem_id,
    mem_nickname,
    mem_birth,
    mem_gender,
    mem_zip,
    mem_addr1,
    mem_addr2,
    mem_email,
    mem_tel

	) VALUES (
	 #{memId,jdbcType=VARCHAR}
	, #{memNickname,jdbcType=VARCHAR}
	, #{memBirth,jdbcType=DATE}
	, #{memGender,jdbcType=VARCHAR}
	, #{memZip,jdbcType=VARCHAR}
	, #{memAddr1,jdbcType=VARCHAR}
	, #{memAddr2,jdbcType=VARCHAR}
	, #{memEmail,jdbcType=VARCHAR}
	, #{memTel,jdbcType=VARCHAR}

	)

INSERT INTO incruiter (
    mem_id,
    cmp_id,
    mem_email,
    mem_tel
) VALUES (
 #{memId,jdbcType=VARCHAR}
, #{cmpId,jdbcType=VARCHAR}
, #{memEmail,jdbcType=VARCHAR}
, #{memTel,jdbcType=VARCHAR}
)

mapper.select:
SELECT A.MEM_ID
FROM MEMBER A
INNER JOIN SEEKER B ON (A.MEM_ID = B.MEM_ID)
WHERE A.MEM_NAME = #{memName}
AND B.MEM_EMAIL = #{memEmail}
