mapper.select:
SELECT ACT_SN, ACT_NAME, ACT_CATEGORY, ACT_PERIOD
FROM ACTIVITY
WHERE MEM_ID = #{memId}

mapper.insert:
INSERT INTO ACTIVITY (
    ACT_SN,
    MEM_ID,
    ACT_NAME,
    ACT_CATEGORY,
    ACT_INSTITUTION,
    ACT_PERIOD,
    ACT_INSERT_DATE,
    ACT_DELETE_DATE,
    ATT_ID
) VALUES (
	#{actSn,jdbcType=VARCHAR}
	, #{memId,jdbcType=VARCHAR}
	, #{actName,jdbcType=VARCHAR}
	, #{actCategory,jdbcType=VARCHAR}
	, #{actInstitution,jdbcType=VARCHAR}
	, #{actPeriod,jdbcType=VARCHAR}
	, SYSDATE
	, #{actDeleteDate,jdbcType=DATE}
	, #{attId,jdbcType=NUMERIC}
)

mapper.update:
UPDATE ACTIVITY
    SET
	    MEM_ID = #{memId,jdbcType=VARCHAR}
	    , ACT_NAME = #{actName,jdbcType=VARCHAR}
	    , ACT_CATEGORY = #{actCategory,jdbcType=VARCHAR}
	    , ACT_INSTITUTION = #{actInstitution,jdbcType=VARCHAR}
	    , ACT_PERIOD = #{actPeriod,jdbcType=VARCHAR}
	    , ACT_DELETE_DATE = #{actDeleteDate,jdbcType=DATE}
	    , ATT_ID = #{attId,jdbcType=NUMERIC}
WHERE
    ACT_SN = #{actSn,jdbcType=VARCHAR}
